import unittest

class TestPositiveControl(unittest.TestCase):

    def test_barrnap_output(self):
        with open("/test_data/ssu/rrna.SRR5481494_sub.gff.checksum", 'r') as f:
            output_hash = f.readlines()[0].split(" ")[0]
        self.assertEqual(output_hash, "d1114ddf936c07d5f3bc947c6156d0c115b7607a1b170af06f72233488752209")

    def test_16S_sequence(self):
        with open("/test_data/ssu//16S.SRR5481494_sub.fna", 'r') as f:
            sequence = f.readlines()[1].strip()
        self.assertEqual(sequence, "")

if __name__ == '__main__':
    unittest.main()
